import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";

const CustomerDetails = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState({});
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, [id]);

  const fetchData = async () => {
    try {
      const [custRes, summaryRes, debitsRes, creditsRes] = await Promise.all([
        axios.get(`/api/admin/customers`),
        axios.get(`/api/admin/customers/${id}`),
        axios.get(`/api/admin/debits/${id}`),
        axios.get(`/api/admin/credits/${id}`)
      ]);

      const found = custRes.data.customers.find((c) => c._id === id);
      setCustomer(found);
      setSummary(summaryRes.data.summary);
      setDebits(debitsRes.data.debits);
      setCredits(creditsRes.data.credits);
    } catch (err) {
      console.error("Error loading customer details:", err);
    } finally {
      setLoading(false);
    }
  };

  const downloadInvoice = async (debitId) => {
    window.open(`/api/admin/debits/${debitId}/invoice`, "_blank");
  };

  if (loading) return <p className="p-4">Loading...</p>;
  if (!customer) return <p className="p-4">Customer not found</p>;

  return (
    <div className="p-6">
         <Link to={`/admin/customers/${customer._id}/add-debit`} className="text-blue-600 hover:underline mb-4 block">
  Add Debit
</Link>

      <h2 className="text-2xl font-bold mb-2">{customer.name}</h2>
      <p>Phone: {customer.phone}</p>
      <p>Address: {customer.address}</p>

      <div className="mt-6">
        <h3 className="text-xl font-semibold mb-2">Khata Summary</h3>
        <p>Total Debit: ₹{summary.debitAmount}</p>
        <p>Total Credit: ₹{summary.creditAmount}</p>
        <p>Balance: ₹{summary.balance}</p>
      </div>

      <div className="mt-6">
        <h3 className="text-lg font-semibold">Debit Entries</h3>
        <table className="w-full mt-2 border">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border">Amount</th>
              <th className="p-2 border">Date</th>
              <th className="p-2 border">Invoice</th>
            </tr>
          </thead>
          <tbody>
            {debits.map((debit) => (
              <tr key={debit._id}>
                <td className="p-2 border">₹{debit.amount}</td>
                <td className="p-2 border">{new Date(debit.date).toLocaleDateString()}</td>
                <td className="p-2 border">
                  <button
                    onClick={() => downloadInvoice(debit._id)}
                    className="text-blue-600 hover:underline"
                  >
                    Download
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-6">
        <h3 className="text-lg font-semibold">Credit Entries</h3>
        <table className="w-full mt-2 border">
          <thead>
            <tr className="bg-gray-100">
              <th className="p-2 border">Amount</th>
              <th className="p-2 border">Method</th>
              <th className="p-2 border">Date</th>
            </tr>
          </thead>
          <tbody>
            {credits.map((credit) => (
              <tr key={credit._id}>
                <td className="p-2 border">₹{credit.amount}</td>
                <td className="p-2 border">{credit.method}</td>
                <td className="p-2 border">{new Date(credit.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CustomerDetails;
