import React, { useState } from 'react';
import axios from 'axios';
import moment from 'moment';
import { useNavigate, useParams } from 'react-router-dom';

const CustomerCreditForm = () => {
  const { customerId } = useParams();
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!amount || isNaN(amount) || Number(amount) <= 0) {
      setMessage('Please enter a valid amount');
      return;
    }

    try {
      setLoading(true);
      setMessage('');

      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/api/customers/${customerId}/credits`,
        {
          amount,
          paymentMethod: 'online',
          date: moment().format('YYYY-MM-DD'),
          paymentDetails: {
            orderId: `ORD-${Date.now()}`,
            paymentId: `PAY-${Date.now()}`,
          }
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          }
        }
      );

      if (response.data.success) {
        // Redirect to dashboard
        navigate('/customerdashboard');
      } else {
        setMessage('Failed to record payment');
      }
    } catch (error) {
      console.error(error);
      setMessage('Error while submitting payment');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="credit-form">
      <h2>Make Online Payment</h2>
      <form onSubmit={handleSubmit}>
        <label>Amount (â‚¹):</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Enter amount"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Pay Now'}
        </button>
      </form>

      {message && <p style={{ color: 'red' }}>{message}</p>}
    </div>
  );
};

export default CustomerCreditForm;
