import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CustomerDashboard() {
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found, please login');
        setLoading(false);
        return;
      }

      try {
        // Fetch customer info, debits, credits
        const res = await fetch('http://localhost:5000/api/customer/me', {
  headers: { 'Authorization': 'Bearer ' + token },
});

        if (!res.ok) throw new Error('Failed to fetch customer data');
        const data = await res.json();
        setCustomer(data.customer);
        setDebits(data.debits);
        setCredits(data.credits);

        // Fetch summary separately
        const sumRes = await fetch('http://localhost:5000/api/customer/me/summary', {
          headers: {
            'Authorization': 'Bearer ' + token,
          },
        });
        if (!sumRes.ok) throw new Error('Failed to fetch summary');
        const sumData = await sumRes.json();
        setSummary(sumData.summary);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/customer/login');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Welcome, {customer.name}</h2>
        <button onClick={handleLogout} style={{ cursor: 'pointer' }}>
          Logout
        </button>
      </div>
      
      <p>Email: {customer.email}</p>
      <p>Phone: {customer.phone || '-'}</p>

      <h3>Khata Summary</h3>
      <ul>
        <li>Total Debits: â‚¹{summary.debitAmount}</li>
        <li>Total Credits: â‚¹{summary.creditAmount}</li>
        <li>Balance: â‚¹{summary.balance}</li>
      </ul>

      <h3>Recent Debits</h3>
      {debits.length === 0 ? <p>No debits yet.</p> : (
        <ul>
          {debits.map(d => (
            <li key={d._id}>{d.description || 'Debit'} - â‚¹{d.amount} - {new Date(d.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}

      <h3>Recent Credits</h3>
      {credits.length === 0 ? <p>No credits yet.</p> : (
        <ul>
          {credits.map(c => (
            <li key={c._id}>{c.description || 'Credit'} - â‚¹{c.amount} - {new Date(c.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
