import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Admin not logged in. Token missing.');
        setLoading(false);
        return;
      }

      try {
        // Fetch summary
        const resSummary = await fetch('http://localhost:5000/api/admin/summary', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });
        const summaryData = await resSummary.json();
        if (!resSummary.ok) throw new Error(summaryData.message || 'Failed to fetch summary.');

        setSummary(summaryData.summary || {});

        // Fetch top 3 customers
        const resCustomers = await fetch('http://localhost:5000/api/admin/customers', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });
        const customerData = await resCustomers.json();
        if (!resCustomers.ok) throw new Error(customerData.message || 'Failed to fetch customers.');

        setCustomers(customerData.customers.slice(0, 3)); // Show only first 3
      } catch (err) {
        setError(err.message || 'Failed to connect to server.');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) return <p>Loading summary...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <Link to="/admin/customers/add"><button>➕ Add New Customer</button></Link>
        <Link to="/admin/debits/add"><button>➕ Add Debit</button></Link>
        <Link to="/admin/credits/add"><button>➕ Add Credit</button></Link>
      </div>

      {summary ? (
        <ul>
          <li>Total Customers: {summary.totalCustomers}</li>
          <li>Total Debits: ₹{summary.totalDebits}</li>
          <li>Total Credits: ₹{summary.totalCredits}</li>
          <li>Balance: ₹{summary.balance}</li>
        </ul>
      ) : (
        <p>Loading summary...</p>
      )}

      <h2 style={{ marginTop: '30px' }}>Recent Customers</h2>
      {customers.length > 0 ? (
        <div>
          <ul>
            {customers.map((cust) => (
              <li key={cust._id}>
                <strong>{cust.name}</strong> ({cust.phone})
              </li>
            ))}
          </ul>
          <Link to="/admin/customers">
            <button style={{ marginTop: '10px' }}>📋 Show More Customers</button>
          </Link>
        </div>
      ) : (
        <p>No customers found.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
