import React, { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const paymentMethods = ["cash", "cheque", "upi", "online"];

const AddCredit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();

  const [amount, setAmount] = useState("");
  const [method, setMethod] = useState(paymentMethods[0]);
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const scriptId = "razorpay-sdk";
    if (!document.getElementById(scriptId)) {
      const script = document.createElement("script");
      script.id = scriptId;
      script.src = "https://checkout.razorpay.com/v1/checkout.js";
      script.async = true;
      document.body.appendChild(script);
    }
  }, []);

  const openRazorpay = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("adminToken") || localStorage.getItem("token");
      if (!token) throw new Error("Authentication token missing.");

      const orderRes = await axios.post(
        "http://localhost:5000/api/payments/create-order",
        { amount: parseFloat(amount) },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (!orderRes.data.success) throw new Error(orderRes.data.message);
      const { order } = orderRes.data;

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: order.amount,
        currency: order.currency,
        name: "Balajee Sales",
        description: "Payment for Credit",
        order_id: order.id,
        handler: async function (response) {
          try {
            const verifyRes = await axios.post(
              "http://localhost:5000/api/payments/verify",
              {
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
                customerId,
                amount: parseFloat(amount),
                paymentMethod: "online",
                date,
              },
              { headers: { Authorization: `Bearer ${token}` } }
            );

            if (verifyRes.data.success) {
              toast.success("Payment successful!");
              if (verifyRes.data.receiptURL) {
                window.open(verifyRes.data.receiptURL, "_blank");
              }
              setAmount("");
              setTimeout(() => navigate(`/admin/customers/${customerId}`), 2000);
            } else {
              toast.error("Payment verification failed.");
            }
          } catch (err) {
            toast.error(err.response?.data?.message || "Error verifying payment");
          } finally {
            setLoading(false);
          }
        },
        theme: {
          color: "#3399cc",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (err) {
      toast.error(err.message || "Failed to initiate payment");
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!amount || amount <= 0) return toast.error("Enter a valid amount");

    const token = localStorage.getItem("adminToken") || localStorage.getItem("token");
    if (!token) return toast.error("Authentication token missing");

    if (method === "online") {
      openRazorpay();
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(
        `http://localhost:5000/api/admin/customers/${customerId}/credits`,
        {
          amount: parseFloat(amount),
          paymentMethod: method,
          date,
          customer: customerId,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      toast.success("Credit added successfully!");
      if (res.data.receiptURL) {
        window.open(res.data.receiptURL, "_blank");
      }

      setTimeout(() => navigate(`/admin/customers/${customerId}`), 1500);
    } catch (err) {
      toast.error(err.response?.data?.message || "Failed to add credit.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <ToastContainer />
      <h2 className="text-xl font-bold mb-4">Add Credit</h2>

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (₹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-2">
          Payment Method
          <select
            value={method}
            onChange={(e) => setMethod(e.target.value)}
            className="w-full border rounded p-2 mt-1"
          >
            {paymentMethods.map((m) => (
              <option key={m} value={m}>
                {m.charAt(0).toUpperCase() + m.slice(1)}
              </option>
            ))}
          </select>
        </label>

        <label className="block mb-4">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <div className="flex justify-between items-center mt-4">
          <button
            type="submit"
            disabled={loading}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            {loading ? "Processing..." : "Add Credit"}
          </button>

          <Link
            to={`/admin/customers/${customerId}`}
            className="text-blue-600 hover:underline text-sm"
          >
            ← Back to Customer
          </Link>
        </div>
      </form>
    </div>
  );
};

export default AddCredit;
