import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const AddDebit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();

  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10)); // yyyy-mm-dd
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMsg("");
    if (!amount || amount <= 0) {
      setError("Please enter a valid positive amount");
      return;
    }

    setLoading(true);
    try {
      const res = await axios.post(`/api/admin/customers/${customerId}/debits`, {
        amount: parseFloat(amount),
        date,
      });

      setSuccessMsg("Debit added successfully! Invoice generated.");
      // Optionally redirect to customer details after 2 seconds
      setTimeout(() => navigate(`/admin/customers/${customerId}`), 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Error adding debit");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Debit Entry</h2>

      {error && <p className="text-red-600 mb-2">{error}</p>}
      {successMsg && <p className="text-green-600 mb-2">{successMsg}</p>}

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (â‚¹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-4">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {loading ? "Saving..." : "Add Debit"}
        </button>
      </form>
    </div>
  );
};

export default AddDebit;
