import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import axios from "axios";

const AddDebit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();

  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [invoiceFile, setInvoiceFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setInvoiceFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!invoiceFile) {
      toast.error("Please select an invoice file to upload.");
      return;
    }

    setLoading(true);

    const token = localStorage.getItem("adminToken");
    const formData = new FormData();
    formData.append("amount", amount);
    formData.append("date", date);
    formData.append("invoice", invoiceFile);

    try {
      const res = await axios.post(
        `http://localhost:5000/api/admin/customers/${customerId}/debits`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      toast.success("Debit added successfully!");
      navigate(`/admin/users/${customerId}`);
    } catch (err) {
      console.error(err);
      const message = err.response?.data?.message || "Failed to add debit.";
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Debit</h2>

      <button
        className="text-sm text-blue-600 underline mb-4"
        onClick={() => navigate(-1)}
      >
        ← Back
      </button>

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (₹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-2">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-4">
          Upload Invoice (PDF/Image)
          <input
            type="file"
            accept=".pdf,image/*"
            onChange={handleFileChange}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <button
          type="submit"
          disabled={loading || !invoiceFile}
          className={`px-4 py-2 rounded text-white ${
            loading || !invoiceFile ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {loading ? "Saving..." : "Add Debit"}
        </button>
      </form>
    </div>
  );
};

export default AddDebit;
