import React, { useEffect, useState } from 'react';

export default function AdminDashboard() {
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchSummary() {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Admin not logged in');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch('/api/admin/summary', {
            headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
        });
        if (!res.ok) throw new Error('Failed to fetch summary');
        const data = await res.json();
        setSummary(data.summary);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchSummary();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <ul>
        <li><strong>Total Customers:</strong> {summary.totalCustomers}</li>
        <li><strong>Total Debits:</strong> ₹{summary.totalDebits}</li>
        <li><strong>Total Credits:</strong> ₹{summary.totalCredits}</li>
        <li><strong>Balance:</strong> ₹{summary.balance}</li>
      </ul>
    </div>
  );
}
