import React, { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const paymentMethods = ["cash", "cheque", "upi", "online"];

const AddCredit = () => {
  const { id: customerId } = useParams();

  const [amount, setAmount] = useState("");
  const [method, setMethod] = useState(paymentMethods[0]);
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMsg("");

    if (!amount || amount <= 0) {
      setError("Please enter a valid amount.");
      return;
    }
    if (!paymentMethods.includes(method)) {
      setError("Please select a valid payment method.");
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.post(
        `/api/admin/customers/${customerId}/credits`,
        { amount: parseFloat(amount), method, date, customer: customerId },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccessMsg("Credit added successfully! Opening receipt...");

      // Open receipt PDF in new tab
      if (res.data && res.data.receiptURL) {
        const url = res.data.receiptURL;
        window.open(url, "_blank");
      }

      // Optionally, clear form or redirect after some time
      setTimeout(() => {
        setSuccessMsg("");
        setAmount("");
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add credit.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Credit</h2>
      {error && <p className="text-red-500 mb-2">{error}</p>}
      {successMsg && <p className="text-green-600 mb-2">{successMsg}</p>}

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (â‚¹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-2">
          Payment Method
          <select
            value={method}
            onChange={(e) => setMethod(e.target.value)}
            className="w-full border rounded p-2 mt-1"
          >
            {paymentMethods.map((m) => (
              <option key={m} value={m}>
                {m.charAt(0).toUpperCase() + m.slice(1)}
              </option>
            ))}
          </select>
        </label>

        <label className="block mb-4">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <button
          type="submit"
          disabled={loading}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {loading ? "Saving..." : "Add Credit"}
        </button>
      </form>
    </div>
  );
};

export default AddCredit;
