import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function CustomerDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {
  const token = localStorage.getItem('adminToken');

        if (!token) throw new Error('Unauthorized: No token found');

        const headers = {
          Authorization: `Bearer ${token}`
        };

        // Fetch customer and summary
        const res = await fetch(`http://localhost:5000/api/admin/customers/${id}`, { headers });
        if (!res.ok) throw new Error('Failed to fetch customer details');
       const data = await res.json();
if (!data.success) throw new Error('Error fetching customer details');
setCustomer(data.customer);
setSummary(data.summary);


        // Fetch debits
        const debitsRes = await fetch(`http://localhost:5000/api/admin/debits/${id}`, { headers });
        if (!debitsRes.ok) throw new Error('Failed to fetch debits');
        const debitsData = await debitsRes.json();
        setDebits(debitsData.debits);

        // Fetch credits
        const creditsRes = await fetch(`http://localhost:5000/api/admin/credits/${id}`, { headers });
        if (!creditsRes.ok) throw new Error('Failed to fetch credits');
        const creditsData = await creditsRes.json();
        setCredits(creditsData.credits);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h2>Customer Details</h2>
      <p><strong>Name:</strong> {customer.name}</p>
      <p><strong>Phone:</strong> {customer.phone || '-'}</p>

      <h3>Khata Summary</h3>
      <ul>
        <li>Total Debits: ₹{summary.debitAmount}</li>
        <li>Total Credits: ₹{summary.creditAmount}</li>
        <li>Balance: ₹{summary.balance}</li>
      </ul>

<h3>Debits</h3>
{debits.length === 0 ? (
  <p>No debits yet.</p>
) : (
  <table border="1" cellPadding="8" style={{ width: '100%', marginBottom: '20px' }}>
    <thead>
      <tr>
        <th>Amount</th>
        <th>Description</th>
        <th>Date</th>
        <th>Invoice</th>
      </tr>
    </thead>
    <tbody>
      {debits.map(d => (
        <tr key={d._id}>
          <td>₹{d.amount}</td>
          <td>{d.description || 'Debit'}</td>
          <td>{new Date(d.date).toLocaleDateString()}</td>
          <td>
            {d.invoiceNumber ? (
              <a
                href={`http://localhost:5000/invoices/${d.invoiceNumber}.pdf`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Download
              </a>
            ) : (
              '-'
            )}
          </td>
        </tr>
      ))}
    </tbody>
  </table>
)}



<h3>Credits</h3>
{credits.length === 0 ? (
  <p>No credits yet.</p>
) : (
  <table border="1" cellPadding="8" style={{ width: '100%' }}>
    <thead>
      <tr>
        <th>Amount</th>
        <th>Payment Method</th>
        <th>Date</th>
        <th>Download Receipt</th>
      </tr>
    </thead>
    <tbody>
      {credits.map(c => (
        <tr key={c._id}>
          <td>₹{c.amount}</td>
          <td>{c.paymentMethod || 'Credit'}</td>
          <td>{new Date(c.date).toLocaleDateString()}</td>
          <td>
            {c.receiptNumber ? (
              <a
                href={`http://localhost:5000/receipts/${c.receiptNumber}.pdf`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Download
              </a>
            ) : (
              '-'
            )}
          </td>
        </tr>
      ))}
    </tbody>
  </table>
)}


    </div>
  );
}
