import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CustomerDashboard() {
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found, please login');
        setLoading(false);
        return;
      }

      try {
        // Fetch customer info, debits, credits
        const res = await fetch('http://localhost:5000/api/customers/me', {
  headers: { 'Authorization': 'Bearer ' + token },
});

        if (!res.ok) throw new Error('Failed to fetch customer data');
        const data = await res.json();
        setCustomer(data.customer);
        setDebits(data.debits);
        setCredits(data.credits);

        // Fetch summary separately
        const sumRes = await fetch('http://localhost:5000/api/customers/me/summary', {
          headers: {
            'Authorization': 'Bearer ' + token,
          },
        });
        if (!sumRes.ok) throw new Error('Failed to fetch summary');
        const sumData = await sumRes.json();
        setSummary(sumData.summary);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/customer/login');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Welcome, {customer.name}</h2>
        <button onClick={handleLogout} style={{ cursor: 'pointer' }}>
          Logout
        </button>
      </div>
      
      <p>Phone: {customer.phone || '-'}</p>

      <h3>Khata Summary</h3>
      <ul>
        <li>Total Debits: ₹{summary.debitAmount}</li>
        <li>Total Credits: ₹{summary.creditAmount}</li>
        <li>Balance: ₹{summary.balance}</li>
      </ul>
<h3>Debits</h3>
{debits.length === 0 ? (
  <p>No debits yet.</p>
) : (
  <table border="1" cellPadding="8" style={{ width: '100%', marginBottom: '20px' }}>
    <thead>
      <tr>
        <th>Amount</th>
        <th>Date</th>
        <th>Invoice</th>
      </tr>
    </thead>
    <tbody>
      {debits.map(d => (
        <tr key={d._id}>
          <td>₹{d.amount}</td>
          
          <td>{new Date(d.date).toLocaleDateString()}</td>
          <td>
            {d.invoiceNumber ? (
              <a
                href={`http://localhost:5000/${d.invoicePdfPath}.pdf`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Download
              </a>
            ) : (
              '-'
            )}
          </td>
        </tr>
      ))}
    </tbody>
  </table>
)}



<h3>Credits</h3>
{credits.length === 0 ? (
  <p>No credits yet.</p>
) : (
  <table border="1" cellPadding="8" style={{ width: '100%' }}>
    <thead>
      <tr>
        <th>Amount</th>
        <th>Payment Method</th>
        <th>Date</th>
        <th>Download Receipt</th>
      </tr>
    </thead>
    <tbody>
      {credits.map(c => (
        <tr key={c._id}>
          <td>₹{c.amount}</td>
          <td>{c.paymentMethod || 'Credit'}</td>
          <td>{new Date(c.date).toLocaleDateString()}</td>
          <td>
            {c.receiptNumber ? (
              <a
                href={`http://localhost:5000/receipts/${c.receiptNumber}.pdf`}
                target="_blank"
                rel="noopener noreferrer"
              >
                Download
              </a>
            ) : (
              '-'
            )}
          </td>
        </tr>
      ))}
    </tbody>
  </table>
)}
    </div>
  );
}
