import React, { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [summary, setSummary] = useState({
    totalCustomers: 0,
    totalDebits: 0,
    totalCredits: 0,
    balance: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSummary = async () => {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Admin not logged in. Token missing.');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch('http://localhost:5000/api/admin/summary', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        const data = await res.json();
        if (!res.ok) {
          throw new Error(data.message || 'Failed to fetch summary.');
        }

        setSummary(data.summary || {});
      } catch (err) {
        setError(err.message || 'Failed to connect to server.');
      } finally {
        setLoading(false);
      }
    };

    fetchSummary();
  }, []);

  if (loading) return <p>Loading summary...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>
      <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
        <li><strong>Total Customers:</strong> {summary.totalCustomers}</li>
        <li><strong>Total Debits:</strong> ₹{summary.totalDebits}</li>
        <li><strong>Total Credits:</strong> ₹{summary.totalCredits}</li>
        <li><strong>Balance:</strong> ₹{summary.balance}</li>
      </ul>
    </div>
  );
};

export default AdminDashboard;
