import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (!token) {
      setError('Admin not logged in. Token missing.');
      setLoading(false);
      return;
    }

    const fetchSummaryAndCustomers = async () => {
      try {
        const [summaryRes, customersRes] = await Promise.all([
          fetch('http://localhost:5000/api/admin/summary', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
          fetch('http://localhost:5000/api/admin/customers', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
        ]);

        const summaryData = await summaryRes.json();
        const customersData = await customersRes.json();

        if (!summaryRes.ok) throw new Error(summaryData.message || 'Failed to fetch summary');
        if (!customersRes.ok) throw new Error(customersData.message || 'Failed to fetch customers');

        setSummary(summaryData.summary || {});
        setCustomers(customersData.customers.slice(0, 3)); // Show top 3 customers
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchSummaryAndCustomers();
  }, []);

  if (loading) return <div>Loading dashboard...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <Link to="/admin/users/add">âž• Add New Customer</Link>
        <Link to="/admin/users">âž• Manage Customers</Link>
        <Link to="/admin/logout">ðŸšª Logout</Link>
      </div>
      {summary && (
        <div>
          <div>Total Customers: {summary.totalCustomers}</div>
          <div>Total Debits: â‚¹{summary.totalDebits}</div>
          <div>Total Credits: â‚¹{summary.totalCredits}</div>
          <div>Balance: â‚¹{summary.balance}</div>
        </div>
      )}
      <div>
        <h2>Top Customers</h2>
        {customers.length > 0 ? (
          <div>
            {customers.map(c => (
              <div key={c.id || c._id}>
                <Link to={`/admin/users/${c.id || c._id}`}>
                  {c.name} â€” {c.phone}
                </Link>
              </div>
            ))}
          </div>
        ) : (
          <div>No customers found.</div>
        )}
        <Link to="/admin/users">ðŸ“„ Show All Customers</Link>
      </div>
    </div>
  );
};

export default AdminDashboard;
