import React, { useEffect, useState } from 'react';

export default function AdminDashboard() {
  const [stats, setStats] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchStats() {
      try {
        const res = await fetch('/api/admin/summary');
        if (!res.ok) throw new Error('Failed to fetch stats');
        const data = await res.json();
        setStats(data.summary);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchStats();
  }, []);

  if (loading) return <div>Loading admin stats...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <ul>
        <li>Total Customers: {stats.totalCustomers}</li>
        <li>Total Debits: ₹{stats.totalDebits}</li>
        <li>Total Credits: ₹{stats.totalCredits}</li>
        <li>Balance: ₹{stats.balance}</li>
      </ul>

      <div>
        <a href="/admin/customers">View Customers</a>
      </div>
    </div>
  );
}
