import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import CustomerLogin from './components/CustomerLogin';
import CustomerDashboard from './components/CustomerDashboard';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import Logout from './components/Logout';
import AdminLogout from './components/AdminLogout';

// Private Route wrapper for admin
function AdminPrivateRoute({ children }) {
  const token = localStorage.getItem('adminToken');
  return token ? children : <Navigate to="/admin/login" />;
}

// Private Route wrapper for customer
function CustomerPrivateRoute({ children }) {
  const token = localStorage.getItem('token');
  const expiry = localStorage.getItem('tokenExpiry');
  if (token && expiry && Date.now() > parseInt(expiry, 10)) {
    // Token expired
    localStorage.removeItem('token');
    localStorage.removeItem('tokenExpiry');
    return <Navigate to="/customer/login" />;
  }
  return token ? children : <Navigate to="/customer/login" />;
}

function App() {
  return (
    <Router>
      <Routes>
        {/* Customer routes */}
        <Route path="/customer/login" element={<CustomerLogin />} />
        <Route
          path="/customer/dashboard"
          element={
            <CustomerPrivateRoute>
              <CustomerDashboard />
            </CustomerPrivateRoute>
          }
        />
        <Route path="/customer/logout" element={<Logout />} />

        {/* Admin routes */}
        <Route path="/admin/login" element={<AdminLogin />} />
        <Route
          path="/admin/dashboard"
          element={
            <AdminPrivateRoute>
              <AdminDashboard />
            </AdminPrivateRoute>
          }
        />
        <Route path="/admin/logout" element={<AdminLogout />} />

        {/* Redirect unknown routes to customer login */}
        <Route path="*" element={<Navigate to="/customer/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
