import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import CustomerLogin from './components/CustomerLogin';
import CustomerList from './components/CustomerList';
import CustomerDetails from './components/CustomerDetails';
import AddDebit from './components/AddDebit';
import AddCredit from './components/AddCredit';

function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || null);

  // Save token to localStorage whenever it changes
  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
    } else {
      localStorage.removeItem('token');
    }
  }, [token]);

  // Simple auth guard for routes
  const RequireAuth = ({ children }) => {
    return token ? children : <Navigate to="/login" replace />;
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<CustomerLogin setToken={setToken} />}
        />
        {/* Protected Routes */}
        <Route
          path="/admin/customers"
          element={
            <RequireAuth>
              <CustomerList />
            </RequireAuth>
          }
        />
        <Route
          path="/admin/customers/:id"
          element={
            <RequireAuth>
              <CustomerDetails />
            </RequireAuth>
          }
        />
        <Route
          path="/admin/customers/:id/add-debit"
          element={
            <RequireAuth>
              <AddDebit />
            </RequireAuth>
          }
        />
        <Route
          path="/admin/customers/:id/add-credit"
          element={
            <RequireAuth>
              <AddCredit />
            </RequireAuth>
          }
        />
        {/* Redirect unknown routes */}
        <Route path="*" element={<Navigate to={token ? "/admin/customers" : "/login"} replace />} />
      </Routes>
    </Router>
  );
}

export default App;
