import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

const AddDebit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [invoiceFile, setInvoiceFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setInvoiceFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!invoiceFile) {
      toast.error("Please select an invoice file to upload.");
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        toast.error("Unauthorized. Please log in again.");
        return;
      }

      const formData = new FormData();
      formData.append("invoice", invoiceFile);

      const res = await axios.post(
        `http://localhost:5000/api/admin/customers/${customerId}/debits`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      toast.success("Debit added successfully!");
      navigate(`/admin/customers/${customerId}`); // ✅ Redirect to CustomerDetails after success
    } catch (err) {
      console.error(err);
      const message = err.response?.data?.message || "Failed to add debit.";
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <h2 className="card-title h4 mb-4">Add Debit</h2>
              
              <button
                type="button"
                className="btn btn-link p-0 mb-4 text-decoration-underline"
                onClick={() => navigate(-1)}
              >
                ← Back
              </button>

              <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="mb-3">
                  <label className="form-label fw-medium">
                    Upload Invoice (PDF)
                  </label>
                  <input
                    type="file"
                    accept=".pdf"
                    onChange={handleFileChange}
                    className="form-control"
                    required
                  />
                </div>

                <button
                  type="submit"
                  disabled={loading || !invoiceFile}
                  className={`btn w-100 ${
                    loading || !invoiceFile
                      ? "btn-secondary"
                      : "btn-primary"
                  }`}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Saving...
                    </>
                  ) : (
                    "Add Debit"
                  )}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddDebit;
