import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";

const CustomerDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState({});
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) {
      navigate("/admin/login");
      return;
    }

    fetchData(token);
  }, [id]);

  const fetchData = async (token) => {
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };

      const [summaryRes, debitsRes, creditsRes] = await Promise.all([
        axios.get(`/api/admin/customers/${id}/summary`, config),
        axios.get(`/api/admin/debits/${id}`, config),
        axios.get(`/api/admin/credits/${id}`, config),
      ]);

      setCustomer(summaryRes.data.customer);
      setSummary(summaryRes.data.summary);
      setDebits(debitsRes.data.debits);
      setCredits(creditsRes.data.credits);
    } catch (error) {
      console.error("Error loading customer data:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p className="p-4">Loading...</p>;
  if (!customer) return <p className="p-4">Customer not found</p>;

  return (
    <div className="p-6">
      <div className="mb-4">
        <Link to={`/admin/customers/${customer._id}/adddebit`} className="btn btn-outline-danger me-2">
          ➕ Add Debit
        </Link>
        <Link to={`/admin/customers/${customer._id}/addcredit`} className="btn btn-outline-success">
          ➕ Add Credit
        </Link>
      </div>

      <h2 className="h4 fw-bold">{customer.name}</h2>
      <p>📞 {customer.phone}</p>
      <p>📍 {customer.address}</p>

      <div className="mt-4">
        <h4>📊 Khata Summary</h4>
        <p>Total Debit: ₹{summary.debitAmount}</p>
        <p>Total Credit: ₹{summary.creditAmount}</p>
        <p>
          Balance:{" "}
          <strong className={summary.balance >= 0 ? "text-danger" : "text-success"}>
            ₹{summary.balance}
          </strong>
        </p>
      </div>

      <div className="mt-4">
        <h5>🧾 Debit Entries</h5>
        <table className="table table-bordered mt-2">
          <thead className="table-light">
            <tr>
              <th>Amount (₹)</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {debits.map((debit) => (
              <tr key={debit._id}>
                <td>{debit.amount}</td>
                <td>{new Date(debit.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-4">
        <h5>💰 Credit Entries</h5>
        <table className="table table-bordered mt-2">
          <thead className="table-light">
            <tr>
              <th>Amount (₹)</th>
              <th>Method</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {credits.map((credit) => (
              <tr key={credit._id}>
                <td>{credit.amount}</td>
                <td>{credit.paymentMethod}</td>
                <td>{new Date(credit.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CustomerDetails;
