import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";

const CustomerDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState({});
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);

useEffect(() => {
  const token = localStorage.getItem("adminToken");
  if (!token) {
    navigate("/admin/login");
    return;
  }

  fetchData(token);
}, [id]); // make sure `id` is in dependency list


  const fetchData = async (token) => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      const [custRes, summaryRes, debitsRes, creditsRes] = await Promise.all([
  axios.get(`/api/admin/customers`, config),
  axios.get(`/api/admin/customers/${id}`, config),
  axios.get(`/api/admin/debits/${id}`, config),
  axios.get(`/api/admin/credits/${id}`, config),
]);

const found = custRes.data.customers.find((c) => c._id === id);
setCustomer(summaryRes.data.customer); // <- from new route response
setSummary(summaryRes.data.summary);
      setDebits(debitsRes.data.debits);
      setCredits(creditsRes.data.credits);j
    } catch (err) {
      console.error("Error loading customer details:", err);
    } finally {
      setLoading(false);
    }
  };

  const downloadInvoice = (debitId) => {
    window.open(`/api/admin/debits/${debitId}/invoice`, "_blank");
  };

  if (loading) return <p className="p-4">Loading...</p>;
  if (!customer) return <p className="p-4">Customer not found</p>;

  return (
    <div className="p-6">
      <Link to={`/admin/customers/${customer._id}/adddebit`} className="btn btn-outline-primary me-2 mb-2">
        Add Debit
      </Link>
      <Link to={`/admin/customers/${customer._id}/addcredit`} className="btn btn-outline-success mb-4">
        Add Credit
      </Link>

      <h2 className="h4 fw-bold mb-2">{customer.name}</h2>
      <p>Phone: {customer.phone}</p>
      <p>Address: {customer.address}</p>

      <div className="mt-4">
        <h4 className="mb-2">Khata Summary</h4>
        <p>Total Debit: ₹{summary.debitAmount}</p>
        <p>Total Credit: ₹{summary.creditAmount}</p>
        <p>Balance: ₹{summary.balance}</p>
      </div>

      <div className="mt-4">
        <h5>Debit Entries</h5>
        <table className="table table-bordered mt-2">
          <thead className="table-light">
            <tr>
              <th>Amount</th>
              <th>Date</th>
              <th>Invoice</th>
            </tr>
          </thead>
          <tbody>
            {debits.map((debit) => (
              <tr key={debit._id}>
                <td>₹{debit.amount}</td>
                <td>{new Date(debit.date).toLocaleDateString()}</td>
                <td>
                  <button onClick={() => downloadInvoice(debit._id)} className="btn btn-sm btn-link">
                    Download
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-4">
        <h5>Credit Entries</h5>
        <table className="table table-bordered mt-2">
          <thead className="table-light">
            <tr>
              <th>Amount</th>
              <th>Method</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {credits.map((credit) => (
              <tr key={credit._id}>
                <td>₹{credit.amount}</td>
                <td>{credit.paymentMethod}</td>
                <td>{new Date(credit.date).toLocaleDateString()}</td>
                <td>
  {credit.receiptNumber ? (
    <a
      href={`/public/receipts/${credit.receiptNumber}.pdf`}
      target="_blank"
      rel="noopener noreferrer"
      className="btn btn-sm btn-link"
    >
      Download
    </a>
  ) : (
    "N/A"
  )}
</td>

              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CustomerDetails;
