import React, { useState, useEffect } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

const AddCredit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  // Detect user role from tokens
  const adminToken = localStorage.getItem("adminToken");
  const customerToken = localStorage.getItem("token");
  const isAdmin = !!adminToken;

  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [paymentMethod, setPaymentMethod] = useState(isAdmin ? "cash" : "");
  const [invoiceFile, setInvoiceFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  // Payment options depending on user type
  const paymentOptions = isAdmin
    ? ["cash", "cheque", "upi", "online", "other"]
    : ["cash", "cheque", "upi", "online"];

  // Handle Razorpay redirect for customers on selecting online payment
  useEffect(() => {
    if (!isAdmin && paymentMethod === "online" && amount > 0) {
      // Here you should call backend API to create Razorpay order & get orderId etc
      // For demo, just redirect to Razorpay payment page
      const razorpayUrl = `/api/payment/razorpay/order?amount=${amount}&customerId=${customerId}`;
      
      // Call backend to get Razorpay payment details
      axios.get(razorpayUrl, {
        headers: { Authorization: `Bearer ${customerToken}` }
      }).then(({ data }) => {
        // data should contain orderId and other Razorpay details
        // Load Razorpay script and open payment form
        const options = {
          key: data.key, // Razorpay key from backend
          amount: data.amount,
          currency: data.currency,
          order_id: data.orderId,
          name: "Balajee Sales",
          description: "Add Credit Payment",
          handler: function (response) {
            // Payment success callback - call backend to confirm payment and save credit
            axios.post(`/api/customer/${customerId}/credits/confirm`, {
              paymentId: response.razorpay_payment_id,
              orderId: response.razorpay_order_id,
              signature: response.razorpay_signature,
              amount,
              date,
              paymentMethod
            }, {
              headers: { Authorization: `Bearer ${customerToken}` }
            }).then(() => {
              alert("Payment successful and credit added!");
              navigate(`/customer/dashboard`);
            }).catch(() => {
              setError("Payment succeeded but confirmation failed.");
            });
          },
          prefill: {
            email: data.email,
            contact: data.contact,
          },
          theme: {
            color: "#3399cc"
          }
        };
        const rzp = new window.Razorpay(options);
        rzp.open();
      }).catch(() => {
        setError("Failed to initiate Razorpay payment.");
      });
    }
  }, [paymentMethod]);

  const handleInvoiceChange = (e) => {
    setInvoiceFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMsg("");

    if (!amount || amount <= 0) {
      setError("Please enter a valid amount.");
      return;
    }
    if (!paymentMethod) {
      setError("Please select a payment method.");
      return;
    }

    if (!isAdmin && paymentMethod === "online") {
      // The Razorpay flow will be triggered by useEffect above, so don't submit form now.
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      formData.append("amount", amount);
      formData.append("date", date);
      formData.append("paymentMethod", paymentMethod);
      if (isAdmin && invoiceFile) {
        formData.append("invoice", invoiceFile);
      }

      const token = isAdmin ? adminToken : customerToken;
      const url = isAdmin
        ? `/api/admin/customers/${customerId}/credits`
        : `/api/customer/customers/${customerId}/credits`;

      await axios.post(url, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data"
        }
      });

      setSuccessMsg("Credit added successfully!");
      setTimeout(() => {
        navigate(isAdmin ? `/admin/customers/${customerId}` : `/customer/dashboard`);
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add credit.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Credit ({isAdmin ? "Admin" : "Customer"})</h2>

      {error && <p className="text-red-500 mb-2">{error}</p>}
      {successMsg && <p className="text-green-600 mb-2">{successMsg}</p>}

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (â‚¹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-4">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-4">
          Payment Method
          <select
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          >
            <option value="" disabled>Select method</option>
            {paymentOptions.map((opt) => (
              <option key={opt} value={opt}>{opt.charAt(0).toUpperCase() + opt.slice(1)}</option>
            ))}
          </select>
        </label>

        {/* Show invoice upload only for admin */}
        {isAdmin && (
          <label className="block mb-4">
            Upload Invoice (optional)
            <input
              type="file"
              accept="application/pdf,image/*"
              onChange={handleInvoiceChange}
              className="w-full border rounded p-2 mt-1"
            />
          </label>
        )}

        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {loading ? "Saving..." : "Add Credit"}
        </button>
      </form>
    </div>
  );
};

export default AddCredit;
