import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSummary = async () => {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Admin not logged in. Token missing.');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch('http://localhost:5000/api/admin/summary', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        const data = await res.json();
        if (!res.ok) {
          throw new Error(data.message || 'Failed to fetch summary.');
        }

        setSummary(data.summary || {});
      } catch (err) {
        setError(err.message || 'Failed to connect to server.');
      } finally {
        setLoading(false);
      }
    };

    fetchSummary();
  }, []);

  if (loading) return <p>Loading summary...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <Link to="/admin/customers/add" style={{ textDecoration: 'none' }}>
          <button>➕ Add New Customer</button>
        </Link>
        <Link to="/admin/debits/add" style={{ textDecoration: 'none' }}>
          <button>➕ Add Debit</button>
        </Link>
        <Link to="/admin/credits/add" style={{ textDecoration: 'none' }}>
          <button>➕ Add Credit</button>
        </Link>
      </div>

      {summary ? (
        <ul>
          <li>Total Customers: {summary.totalCustomers}</li>
          <li>Total Debits: ₹{summary.totalDebits}</li>
          <li>Total Credits: ₹{summary.totalCredits}</li>
          <li>Balance: ₹{summary.balance}</li>
        </ul>
      ) : (
        <p>Loading summary...</p>
      )}
    </div>
  );
};

export default AdminDashboard;
