import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function CustomerDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchData() {
      try {const token = localStorage.getItem('adminToken');

        if (!token) throw new Error('Unauthorized: No token found');

        const headers = {
          Authorization: `Bearer ${token}`
        };

        // Fetch customer and summary
        const res = await fetch(`http://localhost:5000/api/admin/customers/${id}`, { headers });
        if (!res.ok) throw new Error('Failed to fetch customer details');
        const data = await res.json();
        setCustomer(data.customer);
        setSummary(data.summary);

        // Fetch debits
        const debitsRes = await fetch(`http://localhost:5000/api/admin/debits/${id}`, { headers });
        if (!debitsRes.ok) throw new Error('Failed to fetch debits');
        const debitsData = await debitsRes.json();
        setDebits(debitsData.debits);

        // Fetch credits
        const creditsRes = await fetch(`http://localhost:5000/api/admin/credits/${id}`, { headers });
        if (!creditsRes.ok) throw new Error('Failed to fetch credits');
        const creditsData = await creditsRes.json();
        setCredits(creditsData.credits);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h2>Customer Details</h2>
      <p><strong>Name:</strong> {customer.name}</p>
      <p><strong>Email:</strong> {customer.email}</p>
      <p><strong>Phone:</strong> {customer.phone || '-'}</p>

      <h3>Khata Summary</h3>
      <ul>
        <li>Total Debits: ₹{summary.debitAmount}</li>
        <li>Total Credits: ₹{summary.creditAmount}</li>
        <li>Balance: ₹{summary.balance}</li>
      </ul>

      <h3>Recent Debits</h3>
      {debits.length === 0 ? (
        <p>No debits yet.</p>
      ) : (
        <ul>
          {debits.map(d => (
            <li key={d._id}>
              ₹{d.amount} - {d.description || 'Debit'} - {new Date(d.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}

      <h3>Recent Credits</h3>
      {credits.length === 0 ? (
        <p>No credits yet.</p>
      ) : (
        <ul>
          {credits.map(c => (
            <li key={c._id}>
              ₹{c.amount} - {c.paymentMethod || 'Credit'} - {new Date(c.date).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
