import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const AddDebit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();

  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [invoiceFile, setInvoiceFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleFileChange = (e) => {
    setInvoiceFile(e.target.files[0]);
  };

const handleSubmit = async (e) => {
  e.preventDefault();

  const formData = new FormData();
  formData.append('amount', amount);
  formData.append('date', date);
  formData.append('invoice', invoiceFile); // This must match upload.single('invoice')

  try {
    const res = await fetch(`http://localhost:5000/api/admin/customers/${customerId}/debits`, {
      method: 'POST',
      body: formData,
    });

    const data = await res.json();

    if (res.ok) {
      alert('Debit added successfully!');
    } else {
      console.error(data);
      alert(`Error: ${data.message}`);
    }
  } catch (err) {
    console.error('Error while submitting:', err);
    alert('Something went wrong!');
  }
};


  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Debit</h2>
      {error && <p className="text-red-500 mb-2">{error}</p>}
      {successMsg && <p className="text-green-600 mb-2">{successMsg}</p>}

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label className="block mb-2">
          Amount (â‚¹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-2">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-4">
          Upload Invoice (PDF/Image)
          <input
            type="file"
            accept=".pdf,image/*"
            onChange={handleFileChange}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <button
          type="submit"
          disabled={loading}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {loading ? "Saving..." : "Add Debit"}
        </button>
      </form>
    </div>
  );
};

export default AddDebit;
