// src/components/CustomerDashboard.jsx
import React, { useEffect, useState } from 'react';

export default function CustomerDashboard() {
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchData() {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found, please login');
        setLoading(false);
        return;
      }

      try {
        // Fetch customer info, debits, credits
        const res = await fetch('/api/customer/me', {
          headers: {
            'Authorization': 'Bearer ' + token,
          },
        });
        if (!res.ok) throw new Error('Failed to fetch customer data');
        const data = await res.json();
        setCustomer(data.customer);
        setDebits(data.debits);
        setCredits(data.credits);

        // Fetch summary separately
        const sumRes = await fetch('/api/customer/me/summary', {
          headers: {
            'Authorization': 'Bearer ' + token,
          },
        });
        if (!sumRes.ok) throw new Error('Failed to fetch summary');
        const sumData = await sumRes.json();
        setSummary(sumData.summary);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h2>Welcome, {customer.name}</h2>
      <p>Email: {customer.email}</p>
      <p>Phone: {customer.phone || '-'}</p>

      <h3>Khata Summary</h3>
      <ul>
        <li>Total Debits: ₹{summary.debitAmount}</li>
        <li>Total Credits: ₹{summary.creditAmount}</li>
        <li>Balance: ₹{summary.balance}</li>
      </ul>

      <h3>Recent Debits</h3>
      {debits.length === 0 ? <p>No debits yet.</p> : (
        <ul>
          {debits.map(d => (
            <li key={d._id}>{d.description || 'Debit'} - ₹{d.amount} - {new Date(d.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}

      <h3>Recent Credits</h3>
      {credits.length === 0 ? <p>No credits yet.</p> : (
        <ul>
          {credits.map(c => (
            <li key={c._id}>{c.description || 'Credit'} - ₹{c.amount} - {new Date(c.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
