import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Home from './components/Home'; // Homepage component with only Customer Login link
import CustomerLogin from './components/CustomerLogin';
import CustomerDashboard from './components/CustomerDashboard';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard'; // create a placeholder admin dashboard component
import Logout from './components/Logout'; // for customer logout
import AdminLogout from './components/AdminLogout'; // for admin logout

// Private Route wrapper for admin
function AdminPrivateRoute({ children }) {
  const token = localStorage.getItem('adminToken');
  return token ? children : <Navigate to="/admin/login" />;
}

// Private Route wrapper for customer
function CustomerPrivateRoute({ children }) {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/customer/login" />;
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />

        {/* Customer routes */}
        <Route path="/customer/login" element={<CustomerLogin />} />
        <Route
          path="/customer/dashboard"
          element={
            <CustomerPrivateRoute>
              <CustomerDashboard />
            </CustomerPrivateRoute>
          }
        />
        <Route path="/customer/logout" element={<Logout />} />

        {/* Admin routes */}
        <Route path="/admin/login" element={<AdminLogin />} />
        <Route
          path="/admin/dashboard"
          element={
            <AdminPrivateRoute>
              <AdminDashboard />
            </AdminPrivateRoute>
          }
        />
        <Route path="/admin/logout" element={<AdminLogout />} />

        {/* Redirect unknown routes to homepage */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
