import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AdminLogout from './AdminLogout';
import axios from 'axios';

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
  fetch('http://localhost:5000/api/admin/customers', {
    headers: {
      'Authorization': `Bearer ${localStorage.getItem('adminToken')}`,
      'Content-Type': 'application/json',
    },
  })
    .then(res => {
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      return res.json();
    })
    .then(data => {
      setCustomers(data.customers);
      setLoading(false);
    })
    .catch(err => {
      setError(err.message);
      setLoading(false);
    });
}, []);

const handleDelete = async (id) => {
  if (!window.confirm("Are you sure you want to delete this customer?")) return;
  try {
    await axios.delete(`/api/admin/customers/${id}`);
    setCustomers(customers.filter((c) => c._id !== id));
  } catch (err) {
    alert("Failed to delete customer");
  }
};


  if (loading) return <p>Loading customers...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20 }}>
      <header className="d-flex justify-content-between align-items-center mb-3">
  <h2 className="mb-0">Customer List</h2>
  <AdminLogout />
</header>

<section>
  <Link
    to="/admin/customers/add"
    className="btn btn-primary mb-3"
  >
    Add Customer
  </Link>


        {customers.length === 0 ? (
          <p>No customers found.</p>
        ) : (
          <table
            style={{
              borderCollapse: 'collapse',
              width: '100%',
              border: '1px solid #ccc',
            }}
          >
            <thead style={{ backgroundColor: '#f3f4f6' }}>
              <tr>
                <th style={{ padding: 10, border: '1px solid #ccc', textAlign: 'left' }}>Name</th>
                <th style={{ padding: 10, border: '1px solid #ccc', textAlign: 'left' }}>Address</th>
                <th style={{ padding: 10, border: '1px solid #ccc', textAlign: 'left' }}>Phone</th>
                <th style={{ padding: 10, border: '1px solid #ccc', textAlign: 'left' }}>Action</th>
              </tr>
            </thead>
            <tbody>
              {customers.map(({ _id, name, address, phone }) => (
                <tr key={_id}>
                  <td style={{ padding: 10, border: '1px solid #ccc' }}>{name}</td>
                  <td style={{ padding: 10, border: '1px solid #ccc' }}>{address || '-'}</td>
                  <td style={{ padding: 10, border: '1px solid #ccc' }}>{phone}</td>
                  <td style={{ padding: 10, border: '1px solid #ccc' }}>
                    <td>
  <Link to={`/admin/customers/${_id}`} className="btn btn-primary btn-sm me-2">
    View Khata
  </Link>
  <Link to={`/admin/customers/${_id}/edit`} className="btn btn-warning btn-sm me-2">
    Edit
  </Link>
  <button
    className="btn btn-danger btn-sm"
    onClick={() => handleDelete(_id)}
  >
    Delete
  </button>
</td>

                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default CustomerList;
