import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";

const CustomerDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState({});
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
  try {
    const token = localStorage.getItem("adminToken"); // or sessionStorage
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const resCustomer = await axios.get(`http://localhost:5000/api/admin/customers/${id}`, config);
    setCustomer(resCustomer.data.customer);
    setSummary(resCustomer.data.summary);

    const resDebits = await axios.get(`http://localhost:5000/api/admin/debits/${id}`, config);
    setDebits(resDebits.data.debits);

    const resCredits = await axios.get(`http://localhost:5000/api/admin/credits/${id}`, config);
    setCredits(resCredits.data.credits);
  } catch (error) {
    console.error("Error loading customer details: ", error);
  }
};


    fetchData();
  }, [id]);

  if (!customer) return <div>Loading...</div>;

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Customer: {customer.name}</h2>
      <p><strong>Phone:</strong> {customer.phone}</p>
      <p><strong>Address:</strong> {customer.address}</p>

      <h3 className="text-lg font-semibold mt-6">Summary</h3>
      <ul>
        <li>Total Debit: ₹{summary.debitAmount || 0}</li>
        <li>Total Credit: ₹{summary.creditAmount || 0}</li>
        <li>Balance: ₹{summary.balance || 0}</li>
      </ul>

      <h3 className="text-lg font-semibold mt-6">Debit Entries</h3>
      {debits.length > 0 ? (
        <ul className="list-disc list-inside">
          {debits.map((debit) => (
            <li key={debit._id}>
              ₹{debit.amount} on {new Date(debit.date).toLocaleDateString()}
              {debit.invoicePdfPath && (
                <a
                  href={`/${debit.invoicePdfPath}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="ml-2 text-blue-500 underline"
                >
                  View Invoice
                </a>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No debit entries found.</p>
      )}

      <h3 className="text-lg font-semibold mt-6">Credit Entries</h3>
      {credits.length > 0 ? (
        <ul className="list-disc list-inside">
          {credits.map((credit) => (
            <li key={credit._id}>
              ₹{credit.amount} on {new Date(credit.date).toLocaleDateString()} via {credit.paymentMethod}
              {credit.receiptNumber && (
                <a
                  href={`/receipts/${credit.receiptNumber}.pdf`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="ml-2 text-blue-500 underline"
                >
                  View Receipt
                </a>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No credit entries found.</p>
      )}

      <div className="mt-6">
        <Link to={`/add-debit/${id}`} className="btn btn-primary mr-4">Add Debit</Link>
        <Link to={`/add-credit/${id}`} className="btn btn-success">Add Credit</Link>
      </div>
    </div>
  );
};

export default CustomerDetails;
