import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const AddCredit = () => {
  const { id: customerId } = useParams();
  const navigate = useNavigate();

  const [amount, setAmount] = useState("");
  const [method, setMethod] = useState("Cash");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");
  const [isCustomer, setIsCustomer] = useState(false);

  useEffect(() => {
    // Detect user type from tokens or some role info in localStorage
    const adminToken = localStorage.getItem("adminToken");
    const customerToken = localStorage.getItem("token");
    setIsCustomer(!!customerToken && !adminToken);
  }, []);

  // Payment methods based on user type
  const paymentMethods = isCustomer
    ? ["cash", "cheque", "upi", "online"]
    : ["Cash", "Cheque", "UPI", "Online", "Card", "Netbanking"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMsg("");

    if (!amount || amount <= 0) {
      setError("Please enter a valid amount.");
      return;
    }

    if (isCustomer && method === "online") {
      // Redirect to Razorpay payment page (you must implement this backend endpoint or frontend integration)
      // Here we'll assume your backend generates an order and returns Razorpay payment URL

      try {
        setLoading(true);
        const token = localStorage.getItem("token");
        const response = await axios.post(
          `/api/customer/${customerId}/razorpay-order`,
          { amount: parseFloat(amount) },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        const { paymentUrl } = response.data;
        if (paymentUrl) {
          window.location.href = paymentUrl; // redirect to Razorpay checkout
        } else {
          setError("Failed to get payment URL.");
          setLoading(false);
        }
      } catch (err) {
        setError(err.response?.data?.message || "Failed to initiate payment.");
        setLoading(false);
      }
      return;
    }

    // Normal credit add flow
    setLoading(true);
    try {
      const token = isCustomer
        ? localStorage.getItem("token")
        : localStorage.getItem("adminToken");

      const response = await axios.post(
        isCustomer
          ? `/api/customer/customers/${customerId}/credits`
          : `/api/admin/customers/${customerId}/credits`,
        { amount: parseFloat(amount), method, date },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccessMsg("Credit added successfully!");
      // Open invoice PDF for admin only, for customer maybe skip?
      if (!isCustomer) {
        const creditId = response.data.credit._id;
        window.open(`/api/admin/credits/${creditId}/invoice`, "_blank");
      }

      setTimeout(() => navigate(isCustomer ? "/customer/dashboard" : `/admin/customers/${customerId}`), 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add credit.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Add Credit</h2>
      {error && <p className="text-red-500 mb-2">{error}</p>}
      {successMsg && <p className="text-green-600 mb-2">{successMsg}</p>}

      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Amount (â‚¹)
          <input
            type="number"
            step="0.01"
            min="0"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <label className="block mb-2">
          Payment Method
          <select
            value={method}
            onChange={(e) => setMethod(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          >
            {paymentMethods.map((m) => (
              <option key={m} value={m}>
                {m.charAt(0).toUpperCase() + m.slice(1)}
              </option>
            ))}
          </select>
        </label>

        <label className="block mb-4">
          Date
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            required
          />
        </label>

        <button
          type="submit"
          disabled={loading}
          className={`${
            isCustomer ? "bg-green-600 hover:bg-green-700" : "bg-blue-600 hover:bg-blue-700"
          } text-white px-4 py-2 rounded`}
        >
          {loading ? "Processing..." : "Add Credit"}
        </button>
      </form>
    </div>
  );
};

export default AddCredit;
