import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('adminToken');
    if (!token) {
      setError('Admin not logged in. Token missing.');
      setLoading(false);
      return;
    }

    const fetchSummaryAndCustomers = async () => {
      try {
        const [summaryRes, customersRes] = await Promise.all([
          fetch('http://localhost:5000/api/admin/summary', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
          fetch('http://localhost:5000/api/admin/customers', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }),
        ]);

        const summaryData = await summaryRes.json();
        const customersData = await customersRes.json();

        if (!summaryRes.ok) throw new Error(summaryData.message || 'Failed to fetch summary');
        if (!customersRes.ok) throw new Error(customersData.message || 'Failed to fetch customers');

        setSummary(summaryData.summary || {});
        setCustomers(customersData.customers.slice(0, 3)); // Show top 3 customers
      } catch (err) {
        setError(err.message || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchSummaryAndCustomers();
  }, []);

  if (loading) return <p>Loading dashboard...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard</h1>

      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <Link to="/admin/customers/add"><button>➕ Add New Customer</button></Link>
        <Link to="/admin/debits/add"><button>➕ Add Debit</button></Link>
        <Link to="/admin/credits/add"><button>➕ Add Credit</button></Link>
      </div>

      {summary && (
        <ul style={{ marginBottom: '20px' }}>
          <li>Total Customers: {summary.totalCustomers}</li>
          <li>Total Debits: ₹{summary.totalDebits}</li>
          <li>Total Credits: ₹{summary.totalCredits}</li>
          <li>Balance: ₹{summary.balance}</li>
        </ul>
      )}

      <div>
        <h2>Top Customers</h2>
        {customers.length > 0 ? (
          <ul>
            {customers.map(c => (
              <li key={c._id}>{c.name} — {c.phone}</li>
            ))}
          </ul>
        ) : (
          <p>No customers found.</p>
        )}
        <Link to="/admin/customers"><button style={{ marginTop: '10px' }}>📄 Show More</button></Link>
      </div>
    </div>
  );
};

export default AdminDashboard;
