// src/components/AdminDashboard.js

import React, { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSummary = async () => {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Admin not logged in. Token missing.');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/admin/summary', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });

        const data = await response.json();

        if (!response.ok) {
          setError(data.message || 'Failed to fetch summary.');
        } else {
          setSummary(data.summary);
        }
      } catch (err) {
        setError('Failed to connect to server.');
      }
    };

    fetchSummary();
  }, []);

  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h1>Admin Dashboard</h1>
      {summary ? (
        <ul>
          <li>Total Customers: {summary.totalCustomers}</li>
          <li>Total Debits: ₹{summary.totalDebits}</li>
          <li>Total Credits: ₹{summary.totalCredits}</li>
          <li>Balance: ₹{summary.balance}</li>
        </ul>
      ) : (
        <p>Loading summary...</p>
      )}
    </div>
  );
};

export default AdminDashboard;
