import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';

const CustomerDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [customer, setCustomer] = useState(null);
  const [summary, setSummary] = useState(null);
  const [debits, setDebits] = useState([]);
  const [credits, setCredits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('adminToken');
      if (!token) {
        setError('Unauthorized. Please log in.');
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(`http://localhost:5000/api/admin/customers/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!res.ok) throw new Error('Failed to fetch customer');
        const data = await res.json();
        setCustomer(data.customer);

        const summaryRes = await fetch(`http://localhost:5000/api/admin/customers/${id}/summary`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!summaryRes.ok) throw new Error('Failed to fetch summary');
        const summaryData = await summaryRes.json();
        setSummary(summaryData);

        const debitRes = await fetch(`http://localhost:5000/api/admin/debit/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        const creditRes = await fetch(`http://localhost:5000/api/admin/customers/${id}/credits`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        const debitData = await debitRes.json();
        const creditData = await creditRes.json();
        setDebits(debitData);
        setCredits(creditData);
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const handleBack = () => {
    navigate('/admin/customers');
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div style={{ padding: '1rem' }}>
      <button onClick={handleBack} style={{ marginBottom: '1rem' }}>← Back</button>
      <h2>Customer: {customer.name}</h2>
      <p>Email: {customer.email}</p>
      <p>Phone: {customer.phone || 'N/A'}</p>

      <h3>Account Summary</h3>
      <ul>
        <li>Total Debit: ₹{summary?.debitAmount}</li>
        <li>Total Credit: ₹{summary?.creditAmount}</li>
        <li>Balance: ₹{summary?.balance}</li>
      </ul>

      <h3>Debits</h3>
      {debits.length === 0 ? <p>No debits recorded.</p> : (
        <ul>
          {debits.map((d) => (
            <li key={d._id}>{d.description || 'Debit'} - ₹{d.amount} - {new Date(d.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}

      <h3>Credits</h3>
      {credits.length === 0 ? <p>No credits recorded.</p> : (
        <ul>
          {credits.map((c) => (
            <li key={c._id}>{c.description || 'Credit'} - ₹{c.amount} - {new Date(c.date).toLocaleDateString()}</li>
          ))}
        </ul>
      )}

      <div style={{ marginTop: '2rem' }}>
        <Link to={`/admin/customers/${id}/add-debit`}>Add Debit</Link> |{" "}
        <Link to={`/admin/customers/${id}/add-credit`}>Add Credit</Link>
      </div>
    </div>
  );
};

export default CustomerDetails;
