import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CustomerLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const res = await fetch('/api/customer/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || 'Login failed');
        return;
      }

      // Save token in localStorage with expiration
      const token = data.token;
      if (remember) {
        // Save indefinitely until logout
        localStorage.setItem('token', token);
        localStorage.removeItem('tokenExpiry');
      } else {
        // Save token with 4 hours expiry
        localStorage.setItem('token', token);
        const expiryTime = Date.now() + 4 * 60 * 60 * 1000; // 4 hours in ms
        localStorage.setItem('tokenExpiry', expiryTime.toString());
      }

      navigate('/customer/dashboard');
    } catch (err) {
      setError('Something went wrong. Please try again.');
    }
  };

  // On app load, check if token expired and remove it
  React.useEffect(() => {
    const expiry = localStorage.getItem('tokenExpiry');
    if (expiry && Date.now() > parseInt(expiry, 10)) {
      localStorage.removeItem('token');
      localStorage.removeItem('tokenExpiry');
    }
  }, []);

  return (
    <div style={{ maxWidth: 400, margin: 'auto', padding: 20 }}>
      <h2>Customer Login</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: 10 }}>
          <label>Email:</label><br />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: 8 }}
          />
        </div>
        <div style={{ marginBottom: 10 }}>
          <label>Password:</label><br />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: 8 }}
          />
        </div>
        <div style={{ marginBottom: 20 }}>
          <label>
            <input
              type="checkbox"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />{' '}
            Remember Me
          </label>
        </div>
        <button type="submit" style={{ padding: '10px 20px' }}>Login</button>
        {error && <p style={{ color: 'red', marginTop: 10 }}>{error}</p>}
      </form>
    </div>
  );
}
